import { Component, Input, Output, EventEmitter } from '@angular/core';
import { IProduct } from 'src/app/interfaces/Product';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-admin-product',
  templateUrl: './admin-product.component.html',
  styleUrls: ['./admin-product.component.scss']
})
export class AdminProductComponent {
  products: IProduct[] = [];
  removedProducts: IProduct[] = [];

  constructor(private productService: ProductService) {
    this.productService.getProducts().subscribe(data => {
      this.products = data
    }, error => {
      console.log(error.message)
    })
  }
  
  removeItem(id: any) {
  this.productService.deleteProduct(id).subscribe(() => {
    console.log('Delete Success');
    const removedProduct = this.products.find(product => product.id === id);
    if (removedProduct) {
      this.removedProducts.push(removedProduct);
      this.products = this.products.filter(product => product.id !== id);
    }
  });
}

  
}
<ul>
  <li *ngFor="let product of removedProducts">
    {{ product.name }} - {{ product.description }}
  </li>
</ul>
<button class="btn btn-primary" type="submit" routerLink="add">Thêm mới</button>
<table class="table">
    <thead>
      <tr>
        <th scope="col">#</th>
        <th scope="col">Tên sản phẩm</th>
        <th scope="col">Price</th>
        <th scope="col">Ảnh</th>
        <th scope="col">Chức năng</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of products; index as i">
        <th scope="row">{{ i + 1 }}</th>
        <td>{{ product.name }}</td>
        <td>{{ product.price }}</td>
        <td><img [src]="product.img" /></td>
        <td>
          <button class="btn btn-danger " (click)="removeItem(product.id)!">Xóa</button>
          <button class="btn btn-warning" routerLink="{{ product.id }}/edit" >Sửa</button>
      </td>
      </tr>
    </tbody>
  </table>
