import { Component } from '@angular/core';
import { ICategory } from 'src/app/interfaces/Category';
import { CategoryService } from 'src/app/services/category.service';
import { FormBuilder, FormGroup, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';


@Component({
  selector: 'app-admin-category-add',
  templateUrl: './admin-category-add.component.html',
  styleUrls: ['./admin-category-add.component.scss']
})
export class AdminCategoryAddComponent {
  categoryForm = this.formBuilder.group({
    name: ['', [Validators.required, Validators.minLength(4)]],
    img: [''],
  })
  constructor(
    private categoryServerice: CategoryService,
    private router:Router,
    private formBuilder: FormBuilder) { }

    onHandleSubmit() {
      if (this.categoryForm.valid) {
        const category: ICategory = {
          name: this.categoryForm.value.name || "",
          img: this.categoryForm.value.img || "",
        }
        this.categoryServerice.addCategory(category).subscribe(category => {
          alert("Thêm danh mục thành công")
          this.router.navigate(['/admin/category']);
        })
      }}
 
}

<form (ngSubmit)="onHandleSubmit()" [formGroup]="categoryForm">
    <input type="text" formControlName="name" />
    <input type="text" formControlName="img" />

    <button type="submit" [disabled]="!categoryForm.valid">Submit</button>

</form>

<button class="btn btn-primary" type="submit" routerLink="add">Thêm mới</button>
<table class="table">
    <thead>
      <tr>
        <th scope="col">#</th>
        <th scope="col">Tên sản phẩm</th>
        <th scope="col">Price</th>
        <th scope="col">Ảnh</th>
        <th scope="col">Chức năng</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of products; index as i">
        <th scope="row">{{ i + 1 }}</th>
        <td>{{ product.name }}</td>
        <td>{{ product.price }}</td>
        <td><img [src]="product.img" /></td>
        <td>
          <button class="btn btn-danger " (click)="removeItem(product.id)!">Xóa</button>
          <button class="btn btn-warning" routerLink="{{ product.id }}/edit" >Sửa</button>
      </td>
      </tr>
    </tbody>
  </table>
