import { Component, Input, Output, EventEmitter } from '@angular/core';
import { ICategory } from 'src/app/interfaces/Category';
import { CategoryService } from 'src/app/services/category.service';

@Component({
  selector: 'app-admin-category',
  templateUrl: './admin-category.component.html',
  styleUrls: ['./admin-category.component.scss']
})
export class AdminCategoryComponent {
  categorys: ICategory[] = [];

  constructor(private categoryService: CategoryService) {
    this.categoryService.getCategorys().subscribe(data => {
      this.categorys = data
    }, error => {
      console.log(error.message)
    })
  }
  removedItems: ICategory[] = [];

  removeItem(_id: any) {
  this.categoryService.deleteCategory(_id).subscribe(() => {
    console.log('Delete Success');
    const removedItem = this.categorys.find(item => item._id === _id);
    if (removedItem) {
      this.removedItems.push(removedItem);
      this.categorys = this.categorys.filter(item => item._id !== _id);
    }
  });
}

}

<div class="alert alert-primary" role="alert">
 QUẢN LÝ DANH MỤC
</div>
<button class="btn btn-primary" type="submit" routerLink="add">Thêm mới</button>
<table class="table">
    <thead>
      <tr>
        <th scope="col">#</th>
        <th scope="col">Tên Danh mục</th>
        <th scope="col">Ảnh</th>
        <th scope="col">Chức năng</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let category of categorys; index as i">
        <th scope="row">{{ i + 1 }}</th>
        <td>{{ category.name }}</td>
        <td><img [src]="category.img" /></td>
        <td>
          <button class="btn btn-danger " (click)="removeItem(category._id)!">Xóa</button>
          <button class="btn btn-warning" routerLink="{{ category._id }}/edit" >Sửa</button>
      </td>
      </tr>
    </tbody>
  </table>
