import { Component } from '@angular/core';
import { IProduct } from 'src/app/interfaces/Product';
import { ProductService } from 'src/app/services/product.service';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
@Component({
  selector: 'app-admin-product-add',
  templateUrl: './admin-product-add.component.html',
  styleUrls: ['./admin-product-add.component.scss'],
})
export class AdminProductAddComponent {
  constructor(
    private productService: ProductService,
    private formBuilder: FormBuilder
  ) {}

  productForm = this.formBuilder.group({
    name: ['', [Validators.required, Validators.minLength(4)]],
    price: [0],
    img: [''],
  });
  onHandleAdd() {
    if (this.productForm.valid) {
      const product: IProduct = {
        name: this.productForm.value.name || '',
        img: this.productForm.value.img || '',

        price: this.productForm.value.price || 0,
      };
      this.productService.addProduct(product).subscribe((product) => {
        console.log('Thành công', product);
      });
    }
  }
}
<div class="alert alert-info" role="alert">THÊM MỚI SẢN PHẨM</div>
<form (ngSubmit)="onHandleAdd()" [formGroup]="productForm">
  <div class="mb-3">
    <input
      type="text"
      formControlName="name"
      placeholder="Tên"
      class="form-control"
    />
  </div>
  <div class="mb-3">
    <input
      type="number"
      formControlName="price"
      placeholder="Price"
      class="form-control"
    />
  </div>
  <div class="mb-3">
    <input
      type="text"
      formControlName="img"
      placeholder="Ảnh"
      class="form-control"
    />
  </div>

  <button type="submit" class="btn btn-primary bg-block btn-lg">
    Thêm sản phẩm
  </button>
</form>
